    public static Binarisation binariseSignedExpGolombOld(int symbol) {
        if (symbol <= 0) {
            return binariseExpGolomb(Math.abs(symbol) << 1);
        } else {
            return binariseExpGolomb((symbol << 1) - 1);
        }
    }

    public static Binarisation binariseSignedExpGolomb(int symbol) {
        if (symbol + 1024 <= 0 || symbol + 1024 >= _binsSignedArray.length) {
            return binariseSignedExpGolombOld(symbol);
        }
        return new Binarisation(_binsSignedArray[symbol + 1024], _numBinsSignedArray[symbol + 1024]);
    }

    public static int signedExpGolombSize(int symbol) {
        return _numBinsSignedArray[symbol + 1024];
    }

    public static int bitLength(int value) {
        count = 0;
        if(value<0){
            return count;
        }
        if (value > 0x7FFF) {
            value >>= 16;
            count += 16;
        }
        if (value > 0x7F) {
            value >>= 8;
            count += 8;
        }
        if (value > 0x7) {
            value >>= 4;
            count += 4;
        }
        if (value > 0x1) {
            value >>= 2;
            count += 2;
        }
        if (value > 0x0) {
            value >>= 1;
            count += 1;
        }
        return count;
    }

    public static Binarisation binariseExpGolombOld(int symbol) {
        symbol++;
        return new Binarisation(symbol, ((bitLength(symbol) - 1) <<1) + 1);
    }

    public static Binarisation binariseExpGolombOldv1(int symbol) {
        symbol++;
        return new Binarisation(symbol, ((Integer.toBinaryString(symbol).length() - 1) * 2) + 1);
    }

    public static Binarisation binariseExpGolomb(int symbol) {
        //System.out.println(symbol);
        if (symbol < _binsArray.length) {
            return new Binarisation(_binsArray[symbol], _numBinsArray[symbol]);
        } else {
            return binariseExpGolombOld(symbol);
        }
    }

    public static Binarisation binariseTruncatedUnaryNoArray(int symbol, int maxSymbol) {
        if (symbol == maxSymbol) {
            int bins = 0;
            for (int i = 0; i < maxSymbol; i++) {
                bins <<= 1;
                bins += 1;
            }
            return new Binarisation(bins, maxSymbol);
        } else {
            return binariseUnary(symbol);
        }

    }

