    public static int unBinariseUnary(Binarisation binarisation) {
        return binarisation._numBins - 1;
    }

    public static int unBinariseExpGolomb(Binarisation binarisation) {
        return binarisation.getBins() - 1;
    }

    public static int unBinariseSignedExpGolomb(Binarisation binarisation) {
        input = binarisation.getBins() - 1;
        if ((input &0x1) == 0) {
            if (input == 0) {
                return 0;
            } else {
                return -1 * (input >> 1);
            }
        } else {
            return ((input + 1) >> 1);
        }
    }

    public static int unBinariseTruncatedUnary(Binarisation binarisation, int maxSymbol) {
        if (binarisation.getNumbins() == maxSymbol) {
            if ((binarisation.getBins() & 0x1) == 0x1) {
                return maxSymbol;
            } else {
                return maxSymbol - 1;
            }
        } else {
            return unBinariseUnary(binarisation);
        }
    }

    public static boolean isUnary(Binarisation binarisation) {
         bins = binarisation.getBins();
        if (binarisation.getNumbins() == 0 || (bins & 0x1) != 0x0) {
            return false;
        } else {
             l = binarisation.getNumbins();
            for ( i = 1; i < l; i++) {
                if (((bins >>> i) & 0x1) != 0x1) {
                    return false;
                }
            }
            return true;
        }
    }

    //checks if it is exp golomb by checking if the total length of binarisation is the amount of bits needed for the actual value + the expected amount of zeroes before it
    public static boolean isExpGolomb(Binarisation binarisation) {
         bins = binarisation.getBins();
         l = binarisation.getNumbins();
        if (bins == 0 & l == 1) {
            return false;
        }        
        return ((bitLength(bins)) << 1) - 1 == l;
    }

    public static boolean isSignedExpGolomb(Binarisation binarisation) {
        if (binarisation.getBins() == 0) {
            return false;
        }
        if (binarisation.getBins() == 1 && binarisation.getNumbins() == 1) {
            return true;
        }
        return isExpGolomb(binarisation);
    }

    public static boolean isTruncatedUnary(Binarisation binarisation, int maxSymbol) {
        if (binarisation.getNumbins() == maxSymbol) {
            return true;
        } else {
            return isUnary(binarisation);
        }
    }

